package com.ws.ldy.config.result;import com.fasterxml.jackson.annotation.JsonInclude;import lombok.Data;import java.io.Serializable;/** * TODO  返回的数据格式 * * @author 王松 * @WX-QQ 1720696548 * @date 2019/11/14 14:55 */@Datapublic class Result<T> implements Serializable {    private static final long serialVersionUID = -5666504070515657048L;    /**     * 状态码     */    private Integer code;    /**     * 状态描叙     */    private String msg;    /**     * 返回数据     */    @JsonInclude(value = JsonInclude.Include.NON_EMPTY)    private T data;    /**     * TODO    枚举方式     *     * @param resultEnum     * @param data     * @return     * @author ws     * @mail 1720696548@qq.com     * @date 2020/4/19 0019 14:55     */    public Result(ResultEnum resultEnum, T data) {        this.code = resultEnum.getCode();        this.msg = resultEnum.getMsg();        this.data = data;    }    /**     * TODO    code，msg方式     *     * @param code     * @param msg     * @param data     * @return     * @author ws     * @mail 1720696548@qq.com     * @date 2020/4/19 0019 14:55     */    public Result(Integer code, String msg, T data) {        this.code = code;        this.data = data;        this.msg = msg;    }}